import Link from 'next/link'

export default function Index() {
  return (
    <div>
      <p className="eliza">Eliza,</p>
      <p className="question">¿Quieres ser mi novia?</p>
      <div className="links">
        <Link href="/yes">
          <a>Sí</a>
        </Link>
      </div>
      <style jsx global>{`
        html {
          height: 100%;
          width: 100%;
          overflow: hidden;
        }

        body {
          margin: 0;
          padding: 1rem 2rem;

          background: #a8c0ff; /* fallback for old browsers */
          background: -webkit-linear-gradient(to right, #a8c0ff, #3f2b96); /* Chrome 10-25, Safari 5.1-6 */
          background: linear-gradient(to right, #a8c0ff, #3f2b96);
          color: white;
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100%;
        }

        .eliza, .question {
          margin: 0;
          font-size: 100px;
          text-align left;
          max-width: 100%;
        }

        .question {
          font-size: 80px;
        }

        .links {
          width: 100%;
          display: flex;
          justify-content: center;
          align-items: center;
          margin-top: 2rem;
        }

        .links a {
          font-size: 40px;
          color: white;
          text-decoration: none;
          margin: 0 1rem;
          font-family: 'Helvetica';
        }

      `}</style>
    </div>
  )
}
